mapper.update:
UPDATE reservation_info
SET totalPeopleCnt = #{totalPeopleCnt},
    status = #{status}

        , jidone = #{jidone}


        , jidtwo = #{jidtwo}


        , jidthree = #{jidthree}

WHERE toDate = #{toDate}
AND rsvtTime = #{rsvtTime}
AND category = #{category} 
AND rsvfNm = #{rsvfNm}

mapper.select:
SELECT COUNT(*)      
FROM reservation_info
WHERE rsrcId = #{loginId}

SELECT *      
FROM reservation_info
WHERE rsrcId = #{rsrcId}
OR jidone = #{rsrcId}
OR jidtwo = #{rsrcId}
OR jidthree = #{rsrcId}

SELECT COUNT(*) FROM reservation_info
WHERE rsrcId = #{loginId}
    OR jidone = #{loginId}
    OR jidtwo = #{loginId}
    OR jidthree = #{loginId}
